mapper.select:
select ruser, pid, submitTime, result
from statu
where ruser = #{username}

select id,ruser,pid,lang,submitTime,result,timeUsed,memoryUsed,codelen
from statu


        and
        ruser = #{ruser}


        and
        pid = #{pid}


        and
        result = #{result}


        and
        lang = #{language}


order by id desc

    LIMIT #{start}, 50

select COUNT(*)
from statu


        and
        ruser = #{ruser}


        and
        pid = #{pid}


        and
        result = #{result}


        and
        lang = #{language}

SELECT COUNT(*)
FROM statu
WHERE ruser = #{ruser}
